import { Component } from '@angular/core';
import {TestApiService} from "../shared/services/test-api.service";
import {ActivatedRoute, Params} from "@angular/router";
import {Observable, switchMap} from "rxjs";
import {Question, Answer} from "../shared/interfaces/test-interfaces";

@Component({
  selector: 'app-test-page',
  templateUrl: './test-page.component.html',
  styleUrls: ['./test-page.component.css']
})
export class TestPageComponent {
  test$: Observable<Question[]> | undefined
  testId: number 
  questionNumber = 0
  isEnd = false;
  Answers: Answer[] = []
  result: string = ''

  constructor(
    private route: ActivatedRoute,
    private testService: TestApiService
  ) {}

  ngOnInit() {
    this.test$ = this.route.params
      .pipe(switchMap((params: Params) => {

        return this.testService.getTest(params['id'])
      }))
    this.test$.subscribe((p) => {
      console.log(p)
    })
  }

  saveUserResponse(questionId: string, answer: number, testLength: number, ) {
    this.questionNumber++
    this.Answers.push({
      queId: questionId,
      answ: answer
    })
    if (this.questionNumber + 1 === testLength) {
      this.isEnd = true
      this.testService.submitAnswers(
      {
        testUri: string
        selectedAnsws: testId
      }
      )

    }
  }
}
<div class="background">
  <div *ngIf="test$ | async as test; else loading">
    <div *ngIf="isEnd; else question" class="result_wrapper">
      <div class="text"> Ваш результат <div class="result">{{result}}/{{test.length}}</div></div>
      <a class="button_finish" [routerLink]="['/']">ЗАВЕРШИТЬ</a>
    </div>
    <ng-template #question>
      <p class="number">{{test[questionNumber]}}/{{test.length}}</p>
      <div class="wrapper">
        <div class="question">{{test[questionNumber].question}}</div>
        <div class="responses">
          <button *ngFor="let option of test[questionNumber].options; let optionIndex = index"
                  class="response"
                  (click)="saveUserResponse(test[questionNumber].queId, optionIndex, test.length)"
                  >{{option}}</button>
        </div>
      </div>
    </ng-template>
  </div>
  <ng-template #loading>
    <img src="assets/images/loading.gif" alt="loading">
  </ng-template>
</div>
